        /// </summary>
        /// <param name="application">The application to get the Company Address for</param>
        /// <returns>premises, street, locality, towntext, postcode concatenated together as a single string</returns>
        private string GetCompanyAddress(EntityReference application)
        {
            var fetchXml = string.Format(@"<fetch top='1' >
                                              <entity name='defra_application' >
                                                <filter type='and' >
                                                  <condition attribute='defra_applicationid' operator='eq' value='{0}' />
                                                </filter>
                                                <link-entity name='account' from='accountid' to='defra_customerid' >
                                                  <link-entity name='defra_addressdetails' from='defra_customer' to='accountid' >
                                                    <filter name='defra_addressdetails' from='defra_customer' to='accountid' >
                                                      <condition attribute='defra_addresstype' operator='eq' value='910400005' />
                                                    </filter>
                                                    <link-entity name='defra_address' from='defra_addressid' to='defra_address' alias='address' >
                                                      <attribute name='defra_street' />
                                                      <attribute name='defra_name' />
                                                      <attribute name='defra_postcode' />
                                                      <attribute name='defra_locality' />
                                                      <attribute name='defra_premises' />
                                                      <attribute name='defra_towntext' />
                                                    </link-entity>
                                                  </link-entity>
                                                </link-entity>
                                              </entity>
                                            </fetch>", application.Id.ToString());
            var entity = Query.QueryCRMForSingleEntity(Service, fetchXml);
            if (entity == null)
            {
                return string.Empty;
            }
            else
            {
                var address = string.Empty;
                if (entity.Contains("address.defra_premises"))
                {
                    address = (string)entity.GetAttributeValue<AliasedValue>("address.defra_premises").Value;
                }
                if (entity.Contains("address.defra_street"))
                {
                    var street = (string)entity.GetAttributeValue<AliasedValue>("address.defra_street").Value;
                    address = (string.IsNullOrEmpty(street)) ? address : address + Environment.NewLine + street;
                }
                if (entity.Contains("address.defra_locality"))
                {
                    var locality = (string)entity.GetAttributeValue<AliasedValue>("address.defra_locality").Value;
                    address = (string.IsNullOrEmpty(locality)) ? address : address + Environment.NewLine + locality;
                }
                if (entity.Contains("address.defra_towntext"))
                {
                    var town = (string)entity.GetAttributeValue<AliasedValue>("address.defra_towntext").Value;
                    address = (string.IsNullOrEmpty(town)) ? address : address + Environment.NewLine + town;
                }
                if (entity.Contains("address.defra_postcode"))
                {
                    var postcode = (string)entity.GetAttributeValue<AliasedValue>("address.defra_postcode").Value;
                    address = (string.IsNullOrEmpty(postcode)) ? address : address + Environment.NewLine + postcode;
                }
                return address.Trim();
            }
